package cn.licoy.wdog.core.controller.appot;import cn.licoy.wdog.common.annotation.SysLogs;import cn.licoy.wdog.common.bean.ResponseCode;import cn.licoy.wdog.common.bean.ResponseResult;import cn.licoy.wdog.common.controller.AppotBaseController;import cn.licoy.wdog.core.dto.appot.LocusAddDTO;import cn.licoy.wdog.core.entity.appot.Locus;import cn.licoy.wdog.core.service.appot.LocusService;import io.swagger.annotations.Api;import com.alibaba.fastjson.JSONObject;import io.swagger.annotations.ApiOperation;import io.swagger.annotations.ApiParam;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.validation.annotation.Validated;import org.springframework.web.bind.annotation.PostMapping;import org.springframework.web.bind.annotation.RequestBody;import org.springframework.web.bind.annotation.RequestMapping;import org.springframework.web.bind.annotation.RestController;import java.util.Date;/** * @author mc * @version Mon Nov 16 16:20:04 2020 */@RestControllerpublic class LocusController extends AppotBaseController {   @Autowired   private LocusService service;   public LocusService getService() {      return service;   }   @RequestMapping(value = {"/saveLocus"})   @ApiOperation(value = "saveLocus")   @SysLogs("saveLocus")   public ResponseResult saveLocus(Locus locus){       locus.setCreateDate(new Date());       service.insert(locus);   return ResponseResult.e(ResponseCode.OK,null);   }}