package cn.licoy.wdog.core.controller.appot;import cn.licoy.wdog.common.annotation.SysLogs;import cn.licoy.wdog.common.bean.ResponseCode;import cn.licoy.wdog.common.bean.ResponseResult;import cn.licoy.wdog.core.dto.appot.OrderDetailAddDTO;import cn.licoy.wdog.core.service.appot.OrderDetailService;import io.swagger.annotations.Api;import com.alibaba.fastjson.JSONObject;import io.swagger.annotations.ApiOperation;import io.swagger.annotations.ApiParam;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.validation.annotation.Validated;import org.springframework.web.bind.annotation.PostMapping;import org.springframework.web.bind.annotation.RequestBody;import org.springframework.web.bind.annotation.RequestMapping;import org.springframework.web.bind.annotation.RestController;/** * @author mc * @version Fri Sep 25 16:39:55 2020 */@RestController@RequestMapping(value = {""})@Api(tags = {"OrderDetail"})public class OrderDetailController {   @Autowired   private OrderDetailService service;   public OrderDetailService getService() {      return service;   }   @PostMapping(value = {"/saveOrderDetail"})   @ApiOperation(value = "saveOrderDetail")   @SysLogs("saveOrderDetail")   public ResponseResult saveOrderDetail(@RequestBody @Validated @ApiParam(value = "saveOrderDetail") OrderDetailAddDTO addDTO){       JSONObject jsonObject = null;       service.saveOrderDetail(jsonObject);   return ResponseResult.e(ResponseCode.OK,jsonObject);   }}