package cn.licoy.wdog.core.controller.appot;import cn.licoy.wdog.common.annotation.SysLogs;import cn.licoy.wdog.common.bean.ResponseCode;import cn.licoy.wdog.common.bean.ResponseResult;import cn.licoy.wdog.common.controller.AppotBaseController;import cn.licoy.wdog.common.util.GPSSUtils;import cn.licoy.wdog.core.dto.appot.VenueAddDTO;import cn.licoy.wdog.core.entity.appot.Order;import cn.licoy.wdog.core.entity.appot.Venue;import cn.licoy.wdog.core.service.appot.VenueService;import com.baomidou.mybatisplus.mapper.EntityWrapper;import io.swagger.annotations.Api;import com.alibaba.fastjson.JSONObject;import io.swagger.annotations.ApiOperation;import io.swagger.annotations.ApiParam;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.validation.annotation.Validated;import org.springframework.web.bind.annotation.*;import java.util.Collections;import java.util.List;/** * @author mc * @version Fri Sep 25 16:39:55 2020 */@RestControllerpublic class VenueController extends AppotBaseController {   @Autowired   private VenueService service;   public VenueService getService() {      return service;   }   @RequestMapping(value = {"/getVenueById"})   @SysLogs("getVenueById")   public ResponseResult getVenueById(String id){      return ResponseResult.e(ResponseCode.OK,service.selectById(id));   }   @RequestMapping(value = {"/getVenues"})   @SysLogs("getVenues")   public ResponseResult getVenues(double lng1, double lat1 ,String searchInfo){      EntityWrapper<Venue> wrapper = new EntityWrapper<>();      List<Venue> lists = service.selectList(wrapper) ;      for(Venue venue:lists){         venue.setDistance( GPSSUtils.getDistanceFrom2LngLat( lng1,lat1,venue.getLongitude(),venue.getLatitude() )  );      }      Collections.sort( lists );      return ResponseResult.e(ResponseCode.OK,lists);   }}