package cn.licoy.wdog.core.controller.appot;import cn.licoy.wdog.common.annotation.SysLogs;import cn.licoy.wdog.common.bean.ResponseCode;import cn.licoy.wdog.common.bean.ResponseResult;import cn.licoy.wdog.core.dto.appot.VenueDetailAddDTO;import cn.licoy.wdog.core.entity.appot.VenueDetail;import cn.licoy.wdog.core.service.appot.VenueDetailService;import io.swagger.annotations.Api;import com.alibaba.fastjson.JSONObject;import io.swagger.annotations.ApiOperation;import io.swagger.annotations.ApiParam;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.validation.annotation.Validated;import org.springframework.web.bind.annotation.PostMapping;import org.springframework.web.bind.annotation.RequestBody;import org.springframework.web.bind.annotation.RequestMapping;import org.springframework.web.bind.annotation.RestController;/** * @author mc * @version Fri Sep 25 16:39:55 2020 */@RestController@RequestMapping(value = {""})@Api(tags = {"VenueDetail"})public class VenueDetailController {   @Autowired   private VenueDetailService service;   public VenueDetailService getService() {      return service;   }   @PostMapping(value = {"/saveVenueDetail"})   @ApiOperation(value = "saveVenueDetail")   @SysLogs("saveVenueDetail")   public ResponseResult saveVenueDetail(@RequestBody @Validated @ApiParam(value = "saveVenueDetail") VenueDetailAddDTO addDTO){       JSONObject jsonObject = null;       service.saveVenueDetail(jsonObject);   return ResponseResult.e(ResponseCode.OK,jsonObject);   }   @RequestMapping(value = {"/getVenueDetailByVenueId"})   @SysLogs("getVenueDetailByVenueId")   public ResponseResult getVenueDetailByVenueId(String venueId,String sportType){      VenueDetail venueDetail = service.getVenueDetailByVenueId(venueId,sportType) ;      return ResponseResult.e(ResponseCode.OK,venueDetail );   }}