package cn.licoy.wdog.core.controller.appot;import cn.licoy.wdog.common.annotation.SysLogs;import cn.licoy.wdog.common.bean.ResponseCode;import cn.licoy.wdog.common.bean.ResponseResult;import cn.licoy.wdog.core.dto.appot.WechatUserAddDTO;import cn.licoy.wdog.core.entity.appot.WechatUser;import cn.licoy.wdog.core.service.appot.WechatUserService;import io.swagger.annotations.Api;import com.alibaba.fastjson.JSONObject;import io.swagger.annotations.ApiOperation;import io.swagger.annotations.ApiParam;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.validation.annotation.Validated;import org.springframework.web.bind.annotation.*;import javax.servlet.http.HttpServletRequest;/** * @author mc * @version Fri Sep 25 16:39:55 2020 */@RestController@RequestMapping(value = {""})@Api(tags = {"WechatUser"})public class WechatUserController {   @Autowired   private WechatUserService service;   public WechatUserService getService() {      return service;   }   @PostMapping(value = {"/saveWechatUser"})   @ApiOperation(value = "saveWechatUser")   @SysLogs("saveWechatUser")   public ResponseResult saveWechatUser(@RequestBody @Validated @ApiParam(value = "saveWechatUser") WechatUserAddDTO addDTO){       JSONObject jsonObject = null;       service.saveWechatUser(jsonObject);   return ResponseResult.e(ResponseCode.OK,jsonObject);   }   @ResponseBody   @RequestMapping(value = {"/saveorUpdateUser"})   public ResponseResult saveorUpdateUser(WechatUser wechatUser){      service.saveorUpdateUser(wechatUser);      return ResponseResult.e(ResponseCode.OK,wechatUser);   }   @ResponseBody   @RequestMapping(value = {"/getUserByMobile"})   public ResponseResult getUserByMobile(HttpServletRequest request, String mobile) throws Exception {      WechatUser wechatUser = service.getWechatUserByMobile(mobile);      return ResponseResult.e(ResponseCode.OK,wechatUser);   }}