package cn.licoy.wdog.core.entity.appot;import com.baomidou.mybatisplus.annotations.TableId;import com.baomidou.mybatisplus.annotations.TableName;import lombok.AllArgsConstructor;import lombok.Builder;import lombok.Data;import java.math.BigDecimal;import lombok.NoArgsConstructor;import java.io.Serializable;import java.util.Date;/** * @author mc * @version Thu Oct 08 19:59:41 2020 */@Data@NoArgsConstructor@AllArgsConstructor@Builder@TableName(value ="biz_venue")public class Venue implements Serializable ,Comparable<Venue>{@TableId   private String id;   private String venueNo;   private String venueName;   private String venueNameSimple;   private String sportType;   private String addres;   private String tel;   private Double longitude;   private Double latitude;   private String proDesc;   private Date createDate;   private String wxGzh;   private String url;   private String businTime;   private String price;   private String salesPrice;   private String img;   private String province;   private String city;   private String area;   private Double distance;   @Override   public int compareTo(Venue o) {      return Double.valueOf(this.distance - o.distance).intValue();   }   public static void main(String[] args){      BigDecimal bamount = new BigDecimal("0");      bamount.add( new BigDecimal("32.3"));      System.out.println(bamount.add(new BigDecimal("33")));      System.out.println(bamount);   }}